$target = strtolower('{{target}}');
$splittarget = strtolower('{{splittarget}}');
$theme = strtolower('{{theme}}');
if(!empty($style)){
       $style = ' btn-'.$style;
}else{
       $style = '';
}
if(!empty($size)){
       $size = ' btn-'.$size;
}else{
       $size = '';
}

$wrapclass= 'cbbtn';
if(!empty($theme)){
	if($theme == 'flat'){
		$wrapclass= 'cbbtn-flat';
	}
}
if(!empty($icon) && !empty($iconcolor)){
    if($iconcolor == 'white'){
        $iconcolor = ' icon-white';
    }else{
        $iconcolor = '';
    }
}
if(!empty($icon)){
    $icon = '<i class="'.$icon.' '.$iconcolor.'"></i> ';
}else{
    $icon = '';
}
if(!empty($target)){
    if(strtolower($target) == 'this page'){
           $target = '';
    }else{
           $target = 'target="_blank"';
    }
}
if(!empty($splittarget)){
    if(strtolower($splittarget) == 'this page'){
           $splittarget = '';
    }else{
           $splittarget = 'target="_blank"';
    }
}
$splits = '';
